    public static int countRiskWordsPresent(String message)
    {
        String[] riskWords = {"warning","hack","virus","block",
                "suspicious","data loss","spyware","adware",
                "security","breach","error","threat","locked",
                "attention","immediate", "technician","firewall",
                "assistance","risk","diagnose" , "damage", "fail"};
        int count = 0;
        for(String keyword : riskWords)
        {
            if (message.contains(keyword))
            {
                System.out.println(keyword);
                count++;
            }
        }
        return count;
    }

